import React, {useState} from "react";
import './select.css';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import {ClickAwayListener} from "@mui/material";

const Select = () => {
    const [isOpenSelect, setIsOpenSelect] = useState(false);
    const [selectedIndex, setSelectedIndex] = useState(0);
    const [selectedItem, setSelectedItem] = useState('All Categories');
    const openSelect = () => {
        setIsOpenSelect(!isOpenSelect);
    }

    const closeSelect = (index, name) => {
        setSelectedIndex(index);
        setIsOpenSelect(false);
        setSelectedItem(name);
    }
    return (
        <>
            <ClickAwayListener onClickAway={() => setIsOpenSelect(false)}>
                <div className='selectDrop cursor position-relative'>
                    <span className='openSelect' onClick={openSelect}>{selectedItem} {
                        <ExpandMoreIcon></ExpandMoreIcon>}</span>
                    {
                        isOpenSelect === true &&
                        <div className='selectDropdown'>
                            <div className='searchField'>
                                <input type='text' placeholder='Search here'/>
                            </div>
                            <ul className='searchResults'>
                                <li onClick={() => closeSelect(0, 'All Categories')}
                                    className={`${selectedIndex === 0 ? 'active' : ''}`}>All Categories
                                </li>
                                <li onClick={() => closeSelect(1, 'Milks and Dairies')}
                                    className={`${selectedIndex === 1 ? 'active' : ''}`}>Milks and Dairies
                                </li>
                                <li onClick={() => closeSelect(2, 'Wines & Drinks')}
                                    className={`${selectedIndex === 2 ? 'active' : ''}`}>Wines & Drinks
                                </li>
                                <li onClick={() => closeSelect(3, 'Clothing & beauty')}
                                    className={`${selectedIndex === 3 ? 'active' : ''}`}>Clothing & beauty
                                </li>
                                <li onClick={() => closeSelect(4, 'Fresh Seafood')}
                                    className={`${selectedIndex === 4 ? 'active' : ''}`}>Fresh Seafood
                                </li>
                                <li onClick={() => closeSelect(5, 'Pet Foods & Toy')}
                                    className={`${selectedIndex === 5 ? 'active' : ''}`}>Pet Foods & Toy
                                </li>
                                <li onClick={() => closeSelect(6, 'Fast Food')}
                                    className={`${selectedIndex === 6 ? 'active' : ''}`}>Fast Food
                                </li>
                                <li onClick={() => closeSelect(7, 'Baking material')}
                                    className={`${selectedIndex === 7 ? 'active' : ''}`}>Baking material
                                </li>
                                <li onClick={() => closeSelect(8, 'Vegetables')}
                                    className={`${selectedIndex === 8 ? 'active' : ''}`}>Vegetables
                                </li>
                                <li onClick={() => closeSelect(9, 'Fresh Fruit')}
                                    className={`${selectedIndex === 9 ? 'active' : ''}`}>Fresh Fruit
                                </li>
                                <li onClick={() => closeSelect(10, 'Bread and Juice')}
                                    className={`${selectedIndex === 10 ? 'active' : ''}`}>Bread and Juice
                                </li>
                            </ul>
                        </div>
                    }

                </div>
            </ClickAwayListener>
        </>
    )
}

export default Select
