import React, {useState} from "react";
import './select.css';

const Select = () => {
    const [isOpenSelect, setIsOpenSelect] = useState(false);
    const [selectedIndex, setselectedIndex] = useState(0);
    const openSelect = () => {
        setIsOpenSelect (!isOpenSelect);
    }

    const closeSelect = (index) => {
        setselectedIndex (index);
    }
    return (
        <>
            <div className='selectDrop cursor position-relative'>
                <span className='openSelect' onClick={openSelect}>All Categories</span>
                {
                    isOpenSelect === true &&
                    <div className='selectDropdown'>
                        <div className='searchField'>
                            <input type='text'/>
                        </div>
                        <ul className='searchResults'>
                            <li onClick={() => closeSelect(0)} className={`${selectedIndex === '0' ? 'active' : ''}`}>All Categories</li>
                            <li onClick={() => closeSelect(1)}>Milks and Dairies</li>
                            <li onClick={() => closeSelect(2)}>Wines & Drinks</li>
                            <li onClick={() => closeSelect(3)}>Clothing & beauty</li>
                            <li onClick={() => closeSelect(4)}>Fresh Seafood</li>
                            <li onClick={() => closeSelect(5)}>Pet Foods & Toy</li>
                            <li onClick={() => closeSelect(6)}>Fast Food</li>
                            <li onClick={() => closeSelect(7)}>Baking material</li>
                            <li onClick={() => closeSelect(8)}>Vegetables</li>
                            <li onClick={() => closeSelect(9)}>Fresh Fruit</li>
                            <li onClick={() => closeSelect(10)}>Bread and Juice</li>
                        </ul>
                    </div>
                }

            </div>
        </>
    )
}

export default Select
