import React, {useEffect, useState} from "react";
import './header.css';
import Logo from '../../assets/images/logo.svg';
import SearchIcon from '@mui/icons-material/Search';
import Select from "../selectDrop/select";
import axios from "axios";
import PlaceIcon from '@mui/icons-material/Place';
import CompairIcon from '../../assets/images/iconCompare.svg';

const Header = () => {
    const [category, setCategory] = useState([
        'Milks and Dairies',
        'Wines & Drinks',
        'Clothing & beauty',
        'Fresh Seafood',
        'Pet Foods & Toy',
        'Fast Food',
        'Baking material',
        'Vegetables',
        'Fresh Fruit',
        'Bread and Juice'
    ]);

    const countryList = [];

    useEffect(() =>{
        getCountry('https://countriesnow.space/api/v0.1/countries');
    },[]);
    const getCountry = async (url) => {
        try {
            await axios.get(url).then((res) => {
                if (res !== null){
                    res.data.data.map ((item,index)=>{
                        countryList.push(item.country);
                    })
                }
            })
        }catch (error){
            console.log(error.message);
        }
    }
    return (
        <>
            <header>
                <div className='container-fluid'>
                    <div className='row'>
                        <div className='col-sm-2'>
                            <img src={Logo} alt="logo"/>
                        </div>
                        <div className='col-sm-5 pt-2'>
                            <div className='headerSearch d-flex align-items-center'>
                                <Select data={category} placeholder={'All Categories'}></Select>
                                <div className='search'>
                                    <input type='text' placeholder='Search for items...'/>
                                    <SearchIcon className='searchIcon cursor'></SearchIcon>
                                </div>
                            </div>
                        </div>
                        <div className='col-sm-5 d-flex align-items-center'>
                            <div className='ml-auto d-flex align-items-center'>
                                <div className='countryWrapper d-flex align-items-center'>
                                    <PlaceIcon className='locationIcon'/><Select data={countryList}
                                                                                 placeholder={'Your Location'}></Select>
                                </div>
                                <ul className='list list-inline mb-0 headerTabs'>
                                    <li className='list-inline-item'><span><img src={CompairIcon} /> Compare</span></li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </div>
            </header>
        </>

    )
}

export default Header
